function [z0,iter,res,his] = Newton_method( fun, dfun, x0, tol, Nmax )x = x0;x = double(x);fx = fun(x);dfx = dfun(x);iter =0;diff = tol +1;his = zeros(1, Nmax);while (iter < Nmax && diff>= tol)  iter = iter + 1;   diff = - fx/dfx;   x = x + diff;   diff = abs(diff);  fx = fun(x);  dfx = dfun(x);  his(iter) = x;endif (iter == Nmax && diff > tol)  fprintf(['Newton_method stopped for out of range']);enditer = iter;z0 = x; res = abs(fx);his = his(1:iter);return