function [z0,iter,res,his] = chord_method(fun,a,b,tol,Nmax)%The code was inspired by TA section%fun = inline(fun);%read the input his = zeros(1, Nmax);his(1) = b;m = (b - a);n = fun(b) - fun(a);x = n/m;his(2) = b - (fun(b)/ x);iter = 2;err = abs(his(2) - his(1));while (iter <= Nmax && err >= tol)  i = (b - a);  j = fun(b) - fun(a);  k = j/i;  his(iter+1) = his(iter) - fun(his(iter))/k;  err = abs(his(iter+1) - his(iter));  iter = iter +1;  endhis = his(1:iter);iter = iter;z0 = his(iter);res = abs(fun(z0));end