function [x,f,P1,P2,P3,P4]=test_Lagrange_interpolation()% case N = 8n = 8;x = linspace(-1,1,n);for j=1:n  %grid(even)   xj1(j)=-1+((2*j)/n);  yj1(j)=1/(1+(20*xj1(j)^2));  %grid(uneven)   xj2(j)=cos(pi*j/n);  yj2(j)=1/(1+(20*xj2(j)^2));end[P1] = Lagrange_interp(xj1,yj1,x)[P2] = Lagrange_interp(xj2,yj2,x)figure(1)plot(x,yj1,'b',x,P1,'r',xj1, yj1, 'ro')figure(2)plot(x,yj2,'b',x,P2,'r',xj2, yj2, 'ro')% case N = 20n = 20;x = linspace(-1,1,n);for j=1:n  xj3(j)=-1+(2*j/n);  yj3(j)=1/(1+(20*xj3(j)^2));  %grid(uneven)  xj4(j)=cos(pi*j/n);  yj4(j)=1/(1+(20*xj4(j)^2));end[P3] = Lagrange_interp(xj3,yj3,x)[P4] = Lagrange_interp(xj4,yj4,x)figure(3)plot(x,yj3,'b',x,P3,'r',xj3, yj3, 'ro')figure(4)plot(x,yj4,'b',x,P4,'r',xj4, yj4, 'ro')end