function [L1,L2,L3,L4]=test_Lebesgue_function()%N = 8n = 8;x = linspace(-1,1,n);for i=1:n  x1(i)=-1+((2*i)/n);  x2(i)=cos(pi*i/n);endfigure(1)[L1] = compute_Lebesgue_function(x1)figure(2)[L2] = compute_Lebesgue_function(x2)%N = 20;n = 20;x = linspace(-1,1,n);for i=1:n  x3(i)=-1+((2*i)/n);  x4(i)=cos(pi*i/n);endfigure(3)[L3] = compute_Lebesgue_function(x3)figure(4)[L4] = compute_Lebesgue_function(x4)