function [x,p1,p2,p4,p8] = test_least_squares()load('-ascii','AMD_data_2013_2018.dat');%load AMD_data_2013_2018.dat;     %  read data into the my_xy matrixx = AMD_data_2013_2018(:,1);     %  copy first column of my_xy into xy = AMD_data_2013_2018(:,2);x=x';y=y';n = length(x);M=1;p1=0;[a,err] = poly_least_squares(x,y,M);figure(1)plot(x,y, 'b')holdfor j = 1:M+1  p1 = p1 + (a(j) * x.^(j-1));endplot(x, p1, 'r')M=2;p2=0;[a,err] = poly_least_squares(x,y,M);figure(2)plot(x,y, 'b')holdfor j = 1:M+1  p2 = p2 + (a(j) * x.^(j-1));endplot(x, p2, 'r')M=4;p4=0;[a,err] = poly_least_squares(x,y,M);figure(3)plot(x,y, 'b')holdfor j = 1:M+1  p4 = p4 + (a(j) * x.^(j-1));endplot(x, p4, 'r')M=8;p8=0;[a,err] = poly_least_squares(x,y,M);figure(4)plot(x,y, 'b')holdfor j = 1:M+1  p8 = p8 + (a(j) * x.^(j-1));endplot(x, p8, 'r')